const ExerciseSum = ({ courseParts }) => {
  // To-do
  // Form the exercises count array from courseParts as in [{}, {}, {}]
  const courseExercises = courseParts.map(part => part.exercises)
  const sum = courseExercises.reduce(
    (prevExercisesCount, currentCount) => prevExercisesCount + currentCount
  )
  return (
    <h3>total of {sum} exercises</h3>
  )
}

const Part = ({ part }) => {
  return (
    <li>
      {part.name} {part.exercises}
    </li>
  )
}


const Content = ({ courseParts }) => {
  return (
    <div>
      <ul>
        {courseParts.map(part =>
          <Part key={part.id} part={part} />
        )}
      </ul>
    </div>
  )
}

const Header = ({ courseHeader }) => {
  return (
    <>
      <h1>{courseHeader}</h1>
    </>
  )
}

const Course = ({ course }) => {
  return (
    <>
      <Header courseHeader={course.name} />
      <Content courseParts={course.parts} />
      <ExerciseSum courseParts={course.parts} />
    </>
  )
}


const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        },
        {
          name: 'mömmö',
          exercises: 200,
          id: 3
        }
      ]
    }
  ]

  return (
    <div>
      {courses.map(course =>
        <Course key={course.id} course={course} />
      )}
    </div>
  )
}

export default App
